mapper.select:
select 
from WHEFSS_ACC_OUT_SEND
where ACCOUNT_NUM = #{accountNum}
and substr(TIME,1,8) = #{time}
and SEND_STATUS in('00','04')

select time tran_time,sn,account_num,account_to to_account,name_to to_account_name,notice_num,send_status,amount tran_amt,'0' commission
from WHEFSS_ACC_OUT_SEND


        notice_num = #{noticeNum}


        AND sn = #{sn}


        AND account_num = #{accountNum}


        AND time = #{startDate}


        AND time = #{endDate}


        AND flag = #{flag}

